import React, { useEffect, useState } from 'react'
import { useWait } from 'react-wait'
import { Image, StyleSheet, Text, View } from 'react-native'

import CocktailCard from '../components/CocktailCard'

export default function HomeScreen() {
  const { startWaiting, endWaiting, isWaiting } = useWait()
  const [cocktail, setCocktail] = useState(null)

  const fetchCocktail = () => {
    return fetch('http://192.168.1.76:8000/api/cocktail')
      .then(res => res.json())
      .then(cocktail => {
        setCocktail(cocktail)
      })
  }

  useEffect(() => {
    startWaiting('fetching cocktail')
    fetchCocktail()
      .then(() => endWaiting('fetching cocktail'))
      .catch(() => endWaiting('fetching cocktail'))
  }, [])

  return (
    <View style={styles.container}>
      {cocktail
        ? <CocktailCard
          cocktail={cocktail}
          isLoading={isWaiting('fetching cocktail')}
          onSkip={fetchCocktail}
        />
        : 'No cocktail :('}
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    justifyContent: 'center',
  },
})
